THEN
	SELECT CASE WHEN COUNT(*) > 0 
	THEN FALSE ELSE TRUE END AS isAvailable
	FROM section_settings_lt WHERE
	(`session` = 'AM' OR `session` = 'WD')
	AND adviser_id = aAdviserId
	AND schoolyear_id = aSchoolYearId;
END IF;

IF aSession = 'PM'
THEN
	SELECT CASE WHEN COUNT(*) > 0 
	THEN FALSE ELSE TRUE END AS isAvailable
	FROM section_settings_lt WHERE
	(`session` = 'PM' OR `session` = 'WD')
	AND adviser_id = aAdviserId
	AND schoolyear_id = aSchoolYearId;
END IF;

IF aSession = 'WD'
THEN
	SELECT CASE WHEN COUNT(*) > 0 
	THEN FALSE ELSE TRUE END AS isAvailable
	FROM section_settings_lt WHERE
	(`session` = 'AM' OR `session` = 'PM')
	AND adviser_id = aAdviserId
	AND schoolyear_id = aSchoolYearId;
END IF;

END ;;
