CREATE DEFINER=`root`@`localhost` PROCEDURE `getStudentsByKeyword`(
aKeyword VARCHAR(255)
)
BEGIN

SELECT r.*,a.admission_id,
CASE WHEN a.isComplete IS NULL THEN 0
ELSE a.isComplete END AS isAdmissionComplete,
s.student_id,
s.student_no,
CASE WHEN s.isActive IS NULL THEN 0
ELSE s.isActive END AS isStudentActive,
CASE WHEN s.student_type IS NULL THEN r.student_type
ELSE s.student_type
END AS finalStudentType,
CASE WHEN ss.gradelevel_id IS NULL 
THEN r.gradelevel_no 
ELSE (
SELECT g2.grade_level FROM schoolyear_student_lt sslt 
INNER JOIN gradelevel_mt g2 ON sslt.gradelevel_id = g2.gradelevel_id
WHERE sslt.isActiveGradeLevel = 1 LIMIT 1
)
END AS currentGradeLevel
 
FROM registration_mt r 
LEFT JOIN admission_mt a ON a.registration_id = r.registration_id
LEFT JOIN student_mt s ON a.admission_id = s.admission_id 
LEFT JOIN schoolyear_student_lt ss ON s.student_id = ss.student_id
LEFT JOIN gradelevel_mt g ON ss.gradelevel_id = g.gradelevel_id
WHERE 
r.lastName LIKE concat('%',aKeyword,'%') OR
r.registration_id LIKE concat('%',aKeyword,'%') OR 
s.student_no LIKE CONCAT('%',aKeyword,'%')
GROUP BY r.registration_id ;

END ;;
