CREATE DEFINER=`root`@`localhost` PROCEDURE `isFacultyAvailableAdviserFor`(
aSectionId INT, aSession VARCHAR(2), aSectionType VARCHAR(2),
aAdviserId INT, aSchoolYearId INT
)
BEGIN

DECLARE onSameSection BOOLEAN;
DECLARE hasOtherAm BOOLEAN;
DECLARE hasOtherPm BOOLEAN;
DECLARE hasOtherWd BOOLEAN;
DECLARE hasWdOnSameSection BOOLEAN;

SELECT CASE WHEN COUNT(*) = 1 
THEN TRUE ELSE FALSE END INTO onSameSection 
FROM section_settings_lt ss 
WHERE section_id = aSectionId 
AND ss.adviser_id = aAdviserId 
AND ss.schoolyear_id = aSchoolYearId
AND ss.section_type = aSectionType; 

IF aSession = 'AM' THEN
	SELECT CASE WHEN COUNT(*) >= 1 
	THEN TRUE ELSE FALSE END INTO hasOtherAm 
	FROM section_settings_lt ss 
	WHERE section_id != aSectionId 
	AND ss.adviser_id = aAdviserId 
	AND ss.schoolyear_id = aSchoolYearId
	AND ss.`session` = 'AM'; -- hasOtherAM
    
    SELECT CASE WHEN COUNT(*) >= 1
	THEN TRUE ELSE FALSE END INTO hasOtherWd
	FROM section_settings_lt ss
	WHERE section_id != aSectionId 
    AND ss.adviser_id = aAdviserId 
    AND ss.schoolyear_id = aSchoolYearId
	AND ss.`session` = 'WD'; -- hasOtherWD
        
    SELECT (NOT(hasOtherAm) AND NOT(hasOtherWd)) AS isFacultyAvailable;
END IF;

IF aSession = 'PM' THEN
	SELECT CASE WHEN COUNT(*) >= 1 
	THEN TRUE ELSE FALSE END INTO hasOtherPm 
	FROM section_settings_lt ss 
	WHERE section_id != aSectionId 
	AND ss.adviser_id = aAdviserId 
	AND ss.schoolyear_id = aSchoolYearId
	AND ss.`session` = 'PM';
    
    SELECT CASE WHEN COUNT(*) >= 1
	THEN TRUE ELSE FALSE END INTO hasOtherWd
	FROM section_settings_lt ss
	WHERE section_id != aSectionId 
    AND ss.adviser_id = aAdviserId 
    AND ss.schoolyear_id = aSchoolYearId
	AND ss.`session` = 'WD'; -- hasOtherWD

    SELECT (NOT(hasOtherPm) AND NOT(hasOtherWd)) AS isFacultyAvailable;
END IF;


IF aSession = 'WD' THEN
	SELECT CASE WHEN COUNT(*) >= 1 
	THEN TRUE ELSE FALSE END INTO hasOtherAm 
	FROM section_settings_lt ss 
	WHERE section_id != aSectionId 
	AND ss.adviser_id = aAdviserId 
	AND ss.schoolyear_id = aSchoolYearId
	AND ss.`session` = 'AM'; -- hasOtherAM
    
    SELECT CASE WHEN COUNT(*) >= 1 
	THEN TRUE ELSE FALSE END INTO hasOtherPm 
	FROM section_settings_lt ss 
	WHERE section_id != aSectionId 
	AND ss.adviser_id = aAdviserId 
	AND ss.schoolyear_id = aSchoolYearId
	AND ss.`session` = 'PM'; -- hasOtherPM
    
    SELECT CASE WHEN COUNT(*) >= 1
	THEN TRUE ELSE FALSE END INTO hasOtherWd
	FROM section_settings_lt ss
	WHERE section_id != aSectionId 
    AND ss.adviser_id = aAdviserId 
    AND ss.schoolyear_id = aSchoolYearId
	AND ss.`session` = 'WD' 
    AND ss.section_type = aSectionType; -- hasOtherWD
    
    SELECT (NOT(hasOtherPm) AND NOT(hasOtherAm) AND NOT(hasOtherWd)) AS isFacultyAvailable;
END IF;

END ;;
